import logo from './logo.svg';
import './App.css';
import { useEffect } from 'react';

function App() {
  [countries, setcountries]  = useState([]);
  async function makeGetRequest() {
    let unvisitedCountries = {};
    let visitedCountries = {};
    const response = await fetch("https://restcountries.com/v3.1/independent?status=true&fields=name,area,population,region")
        .then(response => response.json())
        .then(data => {
            data.reduce((acc, country) => {
                if(!unvisitedCountries[country.region]){
                    unvisitedCountries[country.region] = [];
                    visitedCountries[country.region] = [];
                }
                unvisitedCountries[country.region].push(country);
                return acc;
            }, unvisitedCountries);
        });
  }
  useEffect(()=> {
    makeGetRequest();
  }, []);
  return (
    <div className="App">

    </div>
  );
}

export default App;
